import React from "react";
import { postings as postingsAPI } from "../../utils/API";
// import { render } from "react-dom";

class User extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: "jbay",
      donationItemHistory: [],
      donationQuantityHistory: [],
      donationDate: [],
    };
  }

  componentDidMount() {
    console.log(this.state.userName + " is mounted");
    postingsAPI
      .findDonations({
        userName: this.state.userName,
      })
      .then((res) => {
        this.setState({
          donationItemHistory: res.data.map(
            (donationObj) => donationObj.donatedItem
          ),
        });
        this.setState({
          donationQuantityHistory: res.data.map(
            (donateObj) => donateObj.quantity
          ),
        });
        this.setState({
          donationDate: res.data.map((timeObj) => timeObj.createdAt),
        });
        console.log(this.state.donationItemHistory);
        console.log(this.state.donationQuantityHistory);
        console.log(this.state.donationDate);
      })
      .catch((e) => console.log(e));
  }

  render() {
    return (
      <div>
        <h2> Recent Donations</h2>
        <p>Item</p>
        <p>Quantity</p>
        <p>Date of Donation</p>
        {this.state.donationItemHistory.map((donatedItem) => (
            <p>{donatedItem}</p>
          ))}
{this.state.donationQuantityHistory.map((donatedQuantity) => (
            <p>{donatedQuantity}</p>
          ))}
{this.state.donationDate.map((donatedDate) => (
            <p>{donatedDate}</p>
          ))}


      </div>
    );
  }
}

export default User;
