import React from "react";
import { postings as postingsAPI } from "../../utils/API";
// import { render } from "react-dom";

class User extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: "jbay",
      donationHistory: [],
    };
  }

  componentDidMount() {
    console.log(this.state.userName + " is mounted");
    postingsAPI
      .findDonations({
        userName: this.state.userName,
      })
      .then((res) => {
        console.log(res.data.map((donationObj) => donationObj.donatedItem))
        this.setState({ donationHistory: res.data.map((donationObj) => donationObj.donatedItem) });

        console.log(this.state.donationHistory);
      })
      .catch((e) => console.log(e));
  }

  render() {
    return (
      <div>
        <h2> Recent Donations</h2>
<p>Item</p>
<p>Quantity</p>
<p>Date of Donation</p>


        <p>{this.state.donationHistory}</p>


      </div>
    );
  }
}

export default User;
