import React from "react";
import { postings as postingsAPI } from "../../utils/API";
import { Redirect } from "react-router-dom";
import User from "../User";
import { withRouter } from "react-router-dom";

class newPosting extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: this.props.user.userName,
      donatedItem: "",
      donatedItemCategory: "",
      quantity: "",
      comments: "",
      donor: this.props.user.donor,
      foodbanker: this.props.user.foodbanker,
    };
  }

  componentDidMount() {
    console.log(this.state.userName);
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    console.log(event.target);
    console.log(event.target.value);
    this.setState({
      [name]: value,
    });
  };

  handleFormSubmit = (event) => {
    event.preventDefault();
    alert("Thanks for donating");
    postingsAPI
      .newPosting({
        userName: this.state.userName,
        donatedItem: this.state.donatedItem,
        donatedItemCategory: this.state.donatedItemCategory,
        quantity: this.state.quantity,
        comments: this.state.comments,
        donor: this.props.user.donor,
        foodbanker: this.props.user.foodbanker,
      })
      .then((res) => {
        this.props.history.push("/user");
      })
      .catch((e) => console.log(e));
  };

  render() {
    if (this.state.donor === "Yes") {
      return (
        <div class="user-profile-container fixed-width">
          <h3 class="h2 border-accent">
            {this.state.userName}, thanks for your donations!
          </h3>
          <h5>Member since 2020</h5>
          <h4 class="h3">Number of items donated</h4>
          {this.state.donatedHistoryNumbers.map((category) => (
            <div>
              <p class="donated-items">
                {Object.keys(category)}: {Object.values(category)}
              </p>
            </div>
          ))}

          <h4>Itemized History</h4>
          {this.state.donatedHistory.map((donatedItem) => (
            <div>
              <p>
                {donatedItem.quantity} {donatedItem.donatedItem} on{" "}
                {donatedItem.date}
              </p>
            </div>
          ))}
        </div>
      );
    } else if (this.state.foodbanker === "Yes");
    return (
      <div class="donate-content fixed-width">
        <h3 class="h2">Claim recently made donations</h3>

        {this.state.donatedHistory.map((donatedItem) => (
          <div class="donate-grid">
            <div class="donate-container">
              <p class="donate-item">
                {donatedItem.userName} donated {donatedItem.quantity}{" "}
                {donatedItem.donatedItem} on {donatedItem.date}
              </p>
              <p class="donate-comment border-accent">
                {" "}
                {donatedItem.comments}{" "}
              </p>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default withRouter(newPosting);
