import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect,
} from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./components/Home";
import SignUp from "./components/SignUp";
import About from "./components/About";
import Posting from "./components/PostingPage";
import Footer from "./components/Footer";
import User from "./components/User";
import ProtectedRoute from "./components/ProtectedRoute";
import SignUpIn from "./components/Tabs";
import { user as userAPI } from "./utils/API";

function App() {
  const [user, setUser] = useState({});
  const [loading, setLoading] = useState(false);
  const [alertInfo, setAlertInfo] = useState({ message: "", theme: "success" });

  useEffect(() => {
    // no catch, add if you want to check for it.
    // only setting user if we got one, to avoid rerendering the page.
    userAPI
      .authenticate()
      .then((res) => (res.data ? setUser(res.data) : 0))
      // repress the authenticate route error if recieved one.
      .catch((e) => console.log(e));
  }, []);

  return (
    <Router>
      <div>
        <Navbar />
        <Route exact path="/" component={Home} />
        <Route
          exact
          path="/signup"
          render={(props) => (
            <SignUpIn
              {...props}
              user={user}
              setUser={setUser}
              setLoading={setLoading}
              setAlertInfo={setAlertInfo}
            />
          )}
          {...{ user, setUser, setLoading, setAlertInfo }}
        />
        <Route exact path="/about" component={About} />
        <Route exact path="/posting" component={Posting} />
        <ProtectedRoute exact path="/user" {...{user, loading, Component: User} } />
        <Footer />
      </div>
    </Router>


  );
}

export default App;
