import React from "react";
import { postings as postingAPI } from "../../utils/API";

class newPosting extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: "testuser",
      donatedItem: "",
      quantity: "",

    };
  }

handleFormSubmit = (event) => {
  event.preventDefault();
  alert("Thanks for donating");
};


  render() {
    return (
      <div>
        <h3>Welcome {this.state.userName}</h3>
        <form>
        <label>Item to Donate</label>
        <br />
        <select id="donatedItem" name="donatedItem" value={this.state.donatedItem}>
          <option value="fruits">Fruit</option>
          <option value="cannedGoods">Canned Goods</option>
          <option value="householdSupplies">Household Supplies</option>
        </select>
        <br/>
        <label>Quantity to Donate</label>
        <br/>
        <input type="number" id="quantity" name="quantity" value={this.state.quantity}></input>
        <br/>
        <label>Comments</label>
        <br/>
        <input type="text" id="comments" name="comments" value={this.state.comments}></input>
        <br/>
        <input type="submit" onClick={this.handleFormSubmit}></input>
        </form>
      </div>
    );
  }
}

export default newPosting;
