import React from "react";
const db = require("../../models/Postings");


class newPosting extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: "testuser",
      donatedItem: "",
      quantity: "",
      comments: "",
    };
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    console.log(event.target);
    console.log(event.target.value);
    this.setState({
      [name]: value,
    });
  };

  handleFormSubmit = (event) => {
    event.preventDefault();
    alert("Thanks for donating");
    db.create({
      userName: this.state.userName,
      donatedItem: this.state.donatedItem,
      quantity: this.state.quantity,
      comments: this.state.comments,
    })
      .then((posting) => res.json(posting))
      .catch((e) => console.log(e));
  };

  render() {
    return (
      <div>
        <h3>Welcome {this.state.userName}</h3>
        <form>
          <label>Item to Donate</label>
          <br />
          <select
            id="donatedItem"
            name="donatedItem"
            value={this.state.donatedItem}
          >
            <option value="fruits">Fruit</option>
            <option value="cannedGoods">Canned Goods</option>
            <option value="householdSupplies">Household Supplies</option>
          </select>
          <br />
          <label>Quantity to Donate</label>
          <br />
          <input
            type="number"
            id="quantity"
            name="quantity"
            value={this.state.quantity}
          ></input>
          <br />
          <label>Comments</label>
          <br />
          <input
            type="text"
            id="comments"
            name="comments"
            value={this.state.comments}
          ></input>
          <br />
          <input type="submit" onClick={this.handleFormSubmit}></input>
        </form>
      </div>
    );
  }
}

export default newPosting;
